import React, { Component } from 'react';
import Web3 from 'web3';
import Identicon from 'identicon.js';
import './App.css';
import Decentragram from '../abis/Decentragram.json'
import Navbar from './Navbar'
import Main from './Main'


class App extends Component {
  async componentWillMount() {
    await this.loadWeb3()
    await this.loadBlockchainData()
  }

  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert("Non-ethereum browser detected. Try MetaMask..!!")
    }
  }

  async loadBlockchainData() {
    const web3 = window.web3

    // Fetch account details
    const accounts = await web3.eth.getAccounts()
    this.setState({ account: accounts[0] })

    // Get Network ID
    const networkId = await web3.eth.net.getId() // this will return 5777 from Ganache
    const networkData = Decentragram.networks[networkId]
    // Access Contract
    if (networkData) {
      const decentragram = web3.eth.Contract(Decentragram.abi, networkData.address)
      this.setState({ decentragram: decentragram })
      const imageCount = await decentragram.methods.imageCounter().call()
      this.setState({ imageCount })
      this.setState({ loading: false })
    } else {
      window.alert("Contract is not yet deployed..!!")
    }
  }
  captureFile = event => {
    event.preventDefault()
    const file = event.target.files[0]
    const reader = new window.FileReader()
    reader.readAsArrayBuffer(file)

    reader.onloadend = () => {
      this.setState({ buffer: Buffer(reader.result) })
      console.log("Buffer", this.state.buffer)
    }
  }

  uploadImage = description => {
    console.log("Submitting file to IPFS")
    // Adding file to IPFS
    ipfs.add(this.state.buffer, (error, result) => {
      console.log("IPFS Result ", result)
      if (error) {
        console.log("Error", error)
      }
    })
  }
  constructor(props) {
    super(props)
    this.state = {
      account: '',
      decentragram: null,
      images: [],
      loading: true
    }
  }

  render() {
    return (
      <div>
        <Navbar account={this.state.account} />
        {this.state.loading
          ? <div id="loader" className="text-center mt-5"><p>Loading...</p></div>
          : <Main
            captureFile={this.captureFile}
          />
        }
      </div>
    );
  }
}

export default App;
